import { useState, useEffect } from 'react';
import { EmlTeamsBoard } from '@api/BoardSDK.js';

const board = new EmlTeamsBoard();

export const useTeams = () => {
  const [teams, setTeams] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchTeams = async () => {
    try {
      setLoading(true);
      setError(null);

      const allTeams = [];
      let cursor = null;

      // Fetch all pages
      do {
        const result = await board.items()
          .withColumns(['region', 'tier', 'captain', 'leaguePoints', 'teamLogo'])
          .withPagination(cursor ? { cursor } : { limit: 100 })
          .execute();

        allTeams.push(...result.items);
        cursor = result.cursor;
      } while (cursor);

      setTeams(allTeams);
    } catch (err) {
      console.error('Failed to fetch teams:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTeams();
  }, []);

  return { teams, loading, error, refetch: fetchTeams };
};

import { useState, useEffect } from 'react';
import { EmlScheduleResultsBoard } from '@api/BoardSDK.js';

const board = new EmlScheduleResultsBoard();

export const useMatches = () => {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchMatches = async () => {
    try {
      setLoading(true);
      setError(null);

      const allMatches = [];
      let cursor = null;

      // Fetch all pages
      do {
        const result = await board.items()
          .withColumns(['matchDate', 'status', 'score', 'streamLink', 'participatingTeams'])
          .withPagination(cursor ? { cursor } : { limit: 100 })
          .execute();

        allMatches.push(...result.items);
        cursor = result.cursor;
      } while (cursor);

      setMatches(allMatches);
    } catch (err) {
      console.error('Failed to fetch matches:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchMatches();
  }, []);

  return { matches, loading, error, refetch: fetchMatches };
};

import { Box, HStack, VStack, Text, Badge, Card, Image, Center } from '@chakra-ui/react';
import { Users, Trophy, MapPin } from 'lucide-react';

const tierColors = {
  Master: { bg: 'linear-gradient(135deg, #FFD700 0%, #FFA500 100%)', shadow: '0 8px 24px rgba(255, 215, 0, 0.4)' },
  Diamond: { bg: 'linear-gradient(135deg, #B9F2FF 0%, #0EA5E9 100%)', shadow: '0 8px 24px rgba(14, 165, 233, 0.4)' },
  Platinum: { bg: 'linear-gradient(135deg, #A8B9C7 0%, #6B7FA0 100%)', shadow: '0 8px 24px rgba(107, 127, 160, 0.4)' },
  Gold: { bg: 'linear-gradient(135deg, #FCD34D 0%, #F59E0B 100%)', shadow: '0 8px 24px rgba(245, 158, 11, 0.4)' },
};

const tierImages = {
  Master: 'https://cdn.discordapp.com/emojis/1429992743207309462.webp?size=80',
  Diamond: 'https://cdn.discordapp.com/emojis/1429992740942385152.webp?size=80',
  Platinum: 'https://cdn.discordapp.com/attachments/1460754809064784014/1472871797820096555/content.png?ex=69942641&is=6992d4c1&hm=a4f6ec61690c89480c7235cf454f0ea37d325d40606b93da2b3867c304a59b4c&',
  Gold: 'https://cdn.discordapp.com/attachments/1460754809064784014/1472872310464712775/width240.png?ex=699426bb&is=6992d53b&hm=4e35d0dcaa7ab0d3df98faadaafe3c82b433bebe6456dc44ddd5860c6bd9b1dd&animated=true',
};

const TeamCard = ({ team }) => {
  const tierConfig = tierColors[team.tier] || tierColors.Gold;

  return (
    <Card.Root
      bg="gray.900"
      border="1px solid"
      borderColor="gray.800"
      rounded="2xl"
      overflow="hidden"
      _hover={{ transform: 'translateY(-4px)', boxShadow: 'xl', borderColor: 'purple.700' }}
      transition="all 0.3s"
    >
      <Box position="relative" h="140px" bg="linear-gradient(180deg, rgba(139, 92, 246, 0.15) 0%, rgba(17, 24, 39, 0) 100%)">
        {team.teamLogo?.url ? (
          <Image src={team.teamLogo.url} alt={team.name} objectFit="contain" h="full" w="full" p="6" />
        ) : (
          <Center h="full">
            <Users size={48} color="var(--chakra-colors-gray-700)" />
          </Center>
        )}

        <Box position="absolute" top="3" right="3" bg="blackAlpha.600" backdropFilter="blur(8px)" px="3" py="1.5" rounded="full" boxShadow={tierConfig.shadow}>
          {tierImages[team.tier] ? (
            <HStack gap="1.5">
              <Image src={tierImages[team.tier]} alt={team.tier} w="16px" h="16px" objectFit="contain" />
              <Text fontSize="xs" fontWeight="800" color="white" textShadow="0 1px 2px rgba(0,0,0,0.3)">
                {team.tier}
              </Text>
            </HStack>
          ) : (
            <HStack gap="1">
              <Trophy size={12} color="white" />
              <Text fontSize="xs" fontWeight="800" color="white" textShadow="0 1px 2px rgba(0,0,0,0.3)">
                {team.tier}
              </Text>
            </HStack>
          )}
        </Box>
      </Box>

      <Card.Body p="5">
        <VStack align="start" gap="4">
          <VStack align="start" gap="1" w="full">
            <Text fontSize="lg" fontWeight="800" color="white" lineClamp={1}>
              {team.name}
            </Text>
            {team.region && team.region.length > 0 && (
              <HStack gap="1.5" color="gray.400">
                <MapPin size={13} />
                <Text fontSize="xs">{team.region[0]}</Text>
              </HStack>
            )}
          </VStack>

          <HStack justify="space-between" w="full" pt="3" borderTop="1px solid" borderColor="gray.800">
            <VStack align="start" gap="0">
              <Text fontSize="xs" color="gray.500" fontWeight="600">CAPTAIN</Text>
              <Text fontSize="sm" color="gray.200" fontWeight="600">{team.captain || 'TBA'}</Text>
            </VStack>
            <VStack align="end" gap="0">
              <Text fontSize="xs" color="gray.500" fontWeight="600">POINTS</Text>
              <Text fontSize="xl" fontWeight="800" color="purple.400">
                {team.leaguePoints ?? 0}
              </Text>
            </VStack>
          </HStack>
        </VStack>
      </Card.Body>
    </Card.Root>
  );
};

export default TeamCard;

import { Box, HStack, VStack, Text, Badge, Card, Button, Separator } from '@chakra-ui/react';
import { Calendar, ExternalLink, Radio } from 'lucide-react';

const statusConfig = {
  Scheduled: { color: 'green', icon: Calendar },
  Live: { color: 'red', icon: Radio, pulse: true },
  Completed: { color: 'blue', icon: Calendar },
  Disputed: { color: 'purple', icon: Calendar },
  Forfeit: { color: 'gray', icon: Calendar },
};

const MatchCard = ({ match }) => {
  const config = statusConfig[match.status] || statusConfig.Scheduled;
  const Icon = config.icon;

  const teams = match.participatingTeams?.linkedItems || [];
  const team1 = teams[0]?.name || 'TBA';
  const team2 = teams[1]?.name || 'TBA';

  const handleStreamClick = () => {
    if (match.streamLink?.url) {
      window.open(match.streamLink.url, '_blank', 'noopener,noreferrer');
    }
  };

  return (
    <Card.Root
      bg="gray.900"
      border="1px solid"
      borderColor={match.status === 'Live' ? 'red.600' : 'gray.800'}
      rounded="xl"
      overflow="hidden"
      _hover={{ transform: 'translateY(-2px)', boxShadow: 'lg' }}
      transition="all 0.2s"
      position="relative"
    >
      {config.pulse && (
        <Box
          position="absolute"
          top="0"
          left="0"
          right="0"
          h="2px"
          bg="red.500"
          animationStyle="pulse"
          animationDuration="2s"
          animationIterationCount="infinite"
        />
      )}

      <Card.Body p="5">
        <VStack align="stretch" gap="4">
          <HStack justify="space-between">
            <Badge
              colorPalette={config.color}
              px="2.5"
              py="1"
              rounded="full"
              fontSize="xs"
              fontWeight="700"
              textTransform="uppercase"
            >
              <HStack gap="1">
                <Icon size={11} />
                <Text>{match.status}</Text>
              </HStack>
            </Badge>
            {match.matchDate && (
              <Text fontSize="xs" color="gray.500" fontWeight="600">
                {match.matchDate.toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}
              </Text>
            )}
          </HStack>

          <HStack justify="space-between" align="center" gap="4">
            <VStack align="end" flex="1" gap="1">
              <Text fontSize="md" fontWeight="700" color="white" textAlign="right" lineClamp={1}>
                {team1}
              </Text>
            </VStack>

            {match.score ? (
              <Box bg="purple.900" px="4" py="2" rounded="lg" border="1px solid" borderColor="purple.700">
                <Text fontSize="lg" fontWeight="800" color="purple.300" letterSpacing="wider">
                  {match.score}
                </Text>
              </Box>
            ) : (
              <Text fontSize="sm" color="gray.600" fontWeight="700">VS</Text>
            )}

            <VStack align="start" flex="1" gap="1">
              <Text fontSize="md" fontWeight="700" color="white" textAlign="left" lineClamp={1}>
                {team2}
              </Text>
            </VStack>
          </HStack>

          {match.streamLink?.url && (
            <>
              <Separator borderColor="gray.800" />
              <Button
                size="sm"
                colorPalette={match.status === 'Live' ? 'red' : 'purple'}
                rounded="lg"
                fontWeight="700"
                onClick={handleStreamClick}
                w="full"
              >
                <ExternalLink size={14} />
                {match.status === 'Live' ? 'Watch Live' : 'View Stream'}
              </Button>
            </>
          )}
        </VStack>
      </Card.Body>
    </Card.Root>
  );
};

export default MatchCard;

import { Box, HStack, VStack, Text, Badge, Table, Image } from '@chakra-ui/react';
import { Trophy, TrendingUp, Minus, TrendingDown } from 'lucide-react';

const tierColors = {
  Master: 'yellow',
  Diamond: 'cyan',
  Platinum: 'gray',
  Gold: 'orange',
};

const tierImages = {
  Master: 'https://cdn.discordapp.com/emojis/1429992743207309462.webp?size=80',
  Diamond: 'https://cdn.discordapp.com/emojis/1429992740942385152.webp?size=80',
  Platinum: 'https://cdn.discordapp.com/attachments/1460754809064784014/1472871797820096555/content.png?ex=69942641&is=6992d4c1&hm=a4f6ec61690c89480c7235cf454f0ea37d325d40606b93da2b3867c304a59b4c&',
  Gold: 'https://cdn.discordapp.com/attachments/1460754809064784014/1472872310464712775/width240.png?ex=699426bb&is=6992d53b&hm=4e35d0dcaa7ab0d3df98faadaafe3c82b433bebe6456dc44ddd5860c6bd9b1dd&animated=true',
};

const StandingsTable = ({ teams }) => {
  const sortedTeams = [...teams].sort((a, b) => (b.leaguePoints ?? 0) - (a.leaguePoints ?? 0));

  const getRankIcon = (index) => {
    if (index === 0) return <Trophy size={18} color="var(--chakra-colors-yellow-400)" />;
    if (index === 1) return <Trophy size={18} color="var(--chakra-colors-gray-400)" />;
    if (index === 2) return <Trophy size={18} color="var(--chakra-colors-orange-600)" />;
    return null;
  };

  return (
    <Box bg="gray.900" border="1px solid" borderColor="gray.800" rounded="2xl" overflow="hidden">
      <Box bg="purple.900" px="6" py="4" borderBottom="1px solid" borderColor="purple.800">
        <HStack gap="2">
          <Trophy size={20} color="var(--chakra-colors-purple-300)" />
          <Text fontSize="lg" fontWeight="800" color="white">League Standings</Text>
        </HStack>
      </Box>

      <Table.Root size="lg" variant="outline">
        <Table.Header bg="gray.800">
          <Table.Row>
            <Table.ColumnHeader color="gray.400" fontWeight="700" fontSize="xs" textTransform="uppercase">
              Rank
            </Table.ColumnHeader>
            <Table.ColumnHeader color="gray.400" fontWeight="700" fontSize="xs" textTransform="uppercase">
              Team
            </Table.ColumnHeader>
            <Table.ColumnHeader color="gray.400" fontWeight="700" fontSize="xs" textTransform="uppercase">
              Tier
            </Table.ColumnHeader>
            <Table.ColumnHeader color="gray.400" fontWeight="700" fontSize="xs" textTransform="uppercase" textAlign="right">
              Points
            </Table.ColumnHeader>
          </Table.Row>
        </Table.Header>
        <Table.Body>
          {sortedTeams.map((team, index) => (
            <Table.Row
              key={team.id}
              bg={index < 3 ? 'gray.850' : 'transparent'}
              _hover={{ bg: 'gray.800' }}
              transition="background 0.2s"
            >
              <Table.Cell>
                <HStack gap="2">
                  {getRankIcon(index) || (
                    <Text fontSize="md" fontWeight="700" color="gray.500" w="18px" textAlign="center">
                      {index + 1}
                    </Text>
                  )}
                </HStack>
              </Table.Cell>
              <Table.Cell>
                <Text fontSize="md" fontWeight="700" color="white">
                  {team.name}
                </Text>
              </Table.Cell>
              <Table.Cell>
                {tierImages[team.tier] ? (
                  <Image src={tierImages[team.tier]} alt={team.tier} w="32px" h="32px" objectFit="contain" />
                ) : (
                  <Badge
                    colorPalette={tierColors[team.tier] || 'gray'}
                    px="2.5"
                    py="1"
                    rounded="full"
                    fontSize="xs"
                    fontWeight="700"
                  >
                    {team.tier}
                  </Badge>
                )}
              </Table.Cell>
              <Table.Cell textAlign="right">
                <Text fontSize="lg" fontWeight="800" color="purple.400">
                  {team.leaguePoints ?? 0}
                </Text>
              </Table.Cell>
            </Table.Row>
          ))}
        </Table.Body>
      </Table.Root>

      {sortedTeams.length === 0 && (
        <Box py="12" textAlign="center">
          <Text color="gray.500">No teams to display</Text>
        </Box>
      )}
    </Box>
  );
};

export default StandingsTable;

import { Box } from '@chakra-ui/react';
import { useTheme } from './hooks/useTheme';
import Navigation from './components/Navigation';
import Hero from './components/Hero';

const App = () => {
  const { theme, toggleTheme } = useTheme();

  return (
    <Box bg={theme === 'dark' ? 'gray.950' : 'gray.50'} minH="100vh">
      <Navigation theme={theme} onThemeToggle={toggleTheme} />

      <Box pt="60px">
        <Hero theme={theme} />
      </Box>
    </Box>
  );
};

export default App;

import { useState, useEffect } from 'react';

export const useTheme = () => {
  const [theme, setTheme] = useState(() => {
    if (typeof window !== 'undefined') {
      const stored = localStorage.getItem('eml-theme');
      return stored || 'dark';
    }
    return 'dark';
  });

  useEffect(() => {
    localStorage.setItem('eml-theme', theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme(prev => prev === 'dark' ? 'light' : 'dark');
  };

  return { theme, toggleTheme };
};

import { IconButton } from '@chakra-ui/react';
import { Sun, Moon } from 'lucide-react';

const ThemeToggle = ({ theme, onToggle }) => {
  return (
    <IconButton
      onClick={onToggle}
      rounded="full"
      size="sm"
      bg={theme === 'dark' ? 'whiteAlpha.200' : 'blackAlpha.200'}
      backdropFilter="blur(10px)"
      border="1px solid"
      borderColor={theme === 'dark' ? 'whiteAlpha.300' : 'blackAlpha.300'}
      color={theme === 'dark' ? 'orange.300' : 'blue.600'}
      _hover={{
        bg: theme === 'dark' ? 'whiteAlpha.300' : 'blackAlpha.300',
        transform: 'rotate(180deg)',
        borderColor: theme === 'dark' ? 'orange.400' : 'blue.400'
      }}
      transition="all 0.4s ease"
      aria-label={`Switch to ${theme === 'dark' ? 'light' : 'dark'} mode`}
    >
      {theme === 'dark' ? <Sun size={18} /> : <Moon size={18} />}
    </IconButton>
  );
};

export default ThemeToggle;

import { Box, Container, HStack, Button, Menu, Portal, Image, Text } from '@chakra-ui/react';
import { ChevronDown, Trophy, Calendar, Users, MessageCircle } from 'lucide-react';
