                    bg={isDark ? 'gray.900' : 'white'}
                    border="1px solid"
                    borderColor={isDark ? 'gray.700' : 'gray.200'}
                    rounded="xl"
                    boxShadow="2xl"
                    p="2"
                  >
                    <Menu.Item
                      value="announcements"
