                    </Menu.Item>
                    <Menu.Item
                      value="calendar"
                      rounded="lg"
                      color={isDark ? 'white' : 'gray.900'}
                      _hover={{ bg: isDark ? 'whiteAlpha.100' : 'gray.100' }}
