            <Text fontSize={{ base: '2xl', md: '4xl' }} fontWeight="900" color={isDark ? 'white' : 'gray.900'}>
              League Teams
            </Text>
            <Text fontSize="lg" color={isDark ? 'gray.400' : 'gray.600'} maxW="3xl">
