import { useState, useEffect } from 'react';

/**
 * Custom hook to fetch data from Google Sheets
 * @param {string} spreadsheetId - Your Google Sheet ID from the URL
 * @param {string} range - Sheet name and range (e.g., "Teams!A1:H100")
 * @param {string} apiKey - Your Google Sheets API key
 * @returns {object} { data, loading, error, refetch }
 */
export const useGoogleSheets = (spreadsheetId, range, apiKey) => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchData = async () => {
    if (!spreadsheetId || !range || !apiKey) {
      setError('Missing required parameters: spreadsheetId, range, or apiKey');
      setLoading(false);
      return;
    }

    try {
      setLoading(true);
      setError(null);

      const url = `https://sheets.googleapis.com/v4/spreadsheets/${spreadsheetId}/values/${range}?key=${apiKey}`;

      const response = await fetch(url);

      if (!response.ok) {
        throw new Error(`Failed to fetch: ${response.statusText}`);
      }

      const result = await response.json();

      // Transform rows into objects using first row as keys
      const rows = result.values || [];
      if (rows.length === 0) {
        setData([]);
        return;
      }

      const headers = rows[0];
      const dataRows = rows.slice(1);

      const transformedData = dataRows.map((row, index) => {
        const obj = { id: index + 1 };
        headers.forEach((header, i) => {
          obj[header] = row[i] || '';
        });
        return obj;
      });

      setData(transformedData);
    } catch (err) {
      console.error('Google Sheets fetch error:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [spreadsheetId, range, apiKey]);

  return { data, loading, error, refetch: fetchData };
};

/**
 * Hook to fetch EML team data from Google Sheets
 * @param {string} spreadsheetId - Your Google Sheet ID
 * @param {string} apiKey - Your Google Sheets API key
 */
export const useTeamsFromSheets = (spreadsheetId, apiKey) => {
  const { data, loading, error, refetch } = useGoogleSheets(
    spreadsheetId,
    'Teams!A:H', // Adjust range based on your sheet
    apiKey
  );

  // Transform Google Sheets data to match app format
  const teams = data.map(row => ({
    id: row.id,
    name: row['Team Name'] || row.name,
    captain: row.Captain || row.captain,
    coCaptain: row['Co-Captain'] || row.coCaptain,
    tier: row.Tier || row.tier,
    region: row.Region || row.region,
    status: row.Status || row.status,
    leaguePoints: parseInt(row['League Points'] || row.leaguePoints || 0),
    teamLogo: row['Team Logo'] || row.teamLogo,
  }));

  return { teams, loading, error, refetch };
};

/**
 * Hook to fetch match schedule from Google Sheets
 * @param {string} spreadsheetId - Your Google Sheet ID
 * @param {string} apiKey - Your Google Sheets API key
 */
export const useMatchesFromSheets = (spreadsheetId, apiKey) => {
  const { data, loading, error, refetch } = useGoogleSheets(
    spreadsheetId,
    'Matches!A:G', // Adjust range based on your sheet
    apiKey
  );

  // Transform to match format
  const matches = data.map(row => ({
    id: row.id,
    name: row['Match Name'] || row.name,
    homeTeam: row['Home Team'] || row.homeTeam,
    awayTeam: row['Away Team'] || row.awayTeam,
    matchDate: row['Match Date'] ? new Date(row['Match Date']) : null,
    status: row.Status || row.status,
    score: row.Score || row.score,
    streamLink: row['Stream Link'] || row.streamLink,
    caster: row.Caster || row.caster,
  }));

  return { matches, loading, error, refetch };
};

/**
 * Google Sheets Configuration
 * 
 * SETUP INSTRUCTIONS:
 * 1. Get your Spreadsheet ID from the Google Sheets URL:
 *    https://docs.google.com/spreadsheets/d/YOUR_SPREADSHEET_ID/edit
 * 
 * 2. Get an API Key:
 *    - Go to https://console.cloud.google.com/apis/credentials
 *    - Click "Create Credentials"  "API Key"
 *    - Enable "Google Sheets API" for your project
 * 
 * 3. Make your sheet publicly accessible:
 *    - File  Share  Publish to web
 *    OR
 *    - Share  Anyone with the link can view
 * 
 * 4. Update the values below
 */

export const GOOGLE_SHEETS_CONFIG = {
  // Your Google Sheets API Key
  apiKey: 'YOUR_API_KEY_HERE',

  // Your Spreadsheet ID (from the URL)
  spreadsheetId: 'YOUR_SPREADSHEET_ID_HERE',

  // Sheet names and ranges
  ranges: {
    teams: 'Teams!A:H',      // Team roster data
    matches: 'Matches!A:G',  // Match schedule
    standings: 'Standings!A:E', // League standings
  }
};

// Alternative: Use environment variables (recommended for production)
export const getConfig = () => ({
  apiKey: import.meta.env.VITE_GOOGLE_SHEETS_API_KEY || GOOGLE_SHEETS_CONFIG.apiKey,
  spreadsheetId: import.meta.env.VITE_SPREADSHEET_ID || GOOGLE_SHEETS_CONFIG.spreadsheetId,
  ranges: GOOGLE_SHEETS_CONFIG.ranges,
});

import { useTeamsFromSheets } from '../hooks/useGoogleSheets';
import { getConfig } from '../config/sheets';
import { Box, Stack, Heading, Spinner, Center, Text, Table, Badge, Button } from '@chakra-ui/react';
import { AlertCircle, RefreshCw } from 'lucide-react';

/**
 * Example component showing how to use Google Sheets data
 * Replace your existing useTeams() calls with this
 */
const TeamsFromSheetsExample = () => {
  const config = getConfig();
  const { teams, loading, error, refetch } = useTeamsFromSheets(
    config.spreadsheetId,
    config.apiKey
  );

  if (loading) {
    return (
      <Center py="12">
        <Spinner size="lg" />
        <Text ml="4">Loading teams from Google Sheets...</Text>
      </Center>
    );
  }

  if (error) {
    return (
      <Box p="6" bg="red.50" borderRadius="lg" border="1px solid" borderColor="red.200">
        <Stack gap="3">
          <Stack direction="row" gap="2" align="start">
            <AlertCircle size={20} color="red.600" />
            <Stack gap="1">
              <Heading size="sm" color="red.700">Failed to load teams</Heading>
              <Text color="red.600" textStyle="sm">{error}</Text>
              <Text color="red.600" textStyle="xs">
                Make sure your API key and Spreadsheet ID are configured in src/generated/config/sheets.js
              </Text>
            </Stack>
          </Stack>
          <Button size="sm" onClick={refetch} variant="outline" colorPalette="red">
            <RefreshCw size={14} /> Retry
          </Button>
        </Stack>
      </Box>
    );
  }

  if (!teams.length) {
    return (
      <Center py="12">
        <Text color="fg.muted">No teams found in Google Sheets</Text>
      </Center>
    );
  }

  return (
    <Box>
      <Stack gap="4">
        <Stack direction="row" justify="space-between" align="center">
          <Heading size="md">Teams from Google Sheets ({teams.length})</Heading>
          <Button size="sm" onClick={refetch} variant="outline">
            <RefreshCw size={14} /> Refresh
          </Button>
        </Stack>

        <Table.ScrollArea>
          <Table.Root size="sm">
            <Table.Header>
              <Table.Row>
                <Table.ColumnHeader>Team Name</Table.ColumnHeader>
                <Table.ColumnHeader>Captain</Table.ColumnHeader>
                <Table.ColumnHeader>Tier</Table.ColumnHeader>
                <Table.ColumnHeader>Region</Table.ColumnHeader>
                <Table.ColumnHeader>Status</Table.ColumnHeader>
              </Table.Row>
            </Table.Header>
            <Table.Body>
              {teams.map(team => (
                <Table.Row key={team.id}>
                  <Table.Cell fontWeight="600">{team.name}</Table.Cell>
                  <Table.Cell>{team.captain}</Table.Cell>
                  <Table.Cell>
                    <Badge colorPalette={team.tier?.toLowerCase() === 'master' ? 'gold' : 'blue'}>
                      {team.tier}
                    </Badge>
                  </Table.Cell>
                  <Table.Cell>{team.region}</Table.Cell>
                  <Table.Cell>
                    <Badge colorPalette={team.status === 'Active' ? 'green' : 'gray'}>
                      {team.status}
                    </Badge>
                  </Table.Cell>
                </Table.Row>
              ))}
            </Table.Body>
          </Table.Root>
        </Table.ScrollArea>
      </Stack>
    </Box>
  );
};

export default TeamsFromSheetsExample;
